                    oActAsiento.DoQuery("UPDATE OJDT SET \"FolioPref\"='" + esta + "',\"FolioNum\"='" + folio.ToString() + "' where \"BaseRef\"='" + codigo + "' AND \"TransId\"='" + transId + "'");


